import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './EnterPage.css'; // Подключаем стили

const EnterPageRegister = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/enter_page/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });

            if (!response.ok) throw new Error('Ошибка сервера');

            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error during fetch:', error);
        }
    };

    const handleSignIn = () => navigate('/authorization');

    return (
        <div className="register-container">
            <div className="register-card">
                <h2>Регистрация</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="логин"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="submit" className="register-button">
                        Зарегистрироваться
                    </button>
                </form>
                <button className="signin-link" onClick={handleSignIn}>
                    Уже зарегистрирован
                </button>
            </div>
        </div>
    );
};

export default EnterPageRegister;
